@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(220, 13%, 13%);
  --card: hsl(210, 40%, 98%);
  --card-foreground: hsl(220, 13%, 13%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(220, 13%, 13%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(220, 13%, 13%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(220, 14%, 96%);
  --muted-foreground: hsl(220, 13%, 13%);
  --accent: hsl(220, 50%, 94%);
  --accent-foreground: hsl(221, 83%, 53%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(220, 13%, 91%);
  --input: hsl(220, 13%, 97%);
  --ring: hsl(221, 83%, 53%);
  --chart-1: hsl(221, 83%, 53%);
  --chart-2: hsl(160, 100%, 36%);
  --chart-3: hsl(43, 96%, 56%);
  --chart-4: hsl(147, 78%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --sidebar: hsl(210, 40%, 98%);
  --sidebar-foreground: hsl(220, 13%, 13%);
  --sidebar-primary: hsl(221, 83%, 53%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(220, 50%, 94%);
  --sidebar-accent-foreground: hsl(221, 83%, 53%);
  --sidebar-border: hsl(220, 13%, 91%);
  --sidebar-ring: hsl(221, 83%, 53%);
  --font-sans: 'Inter', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Fira Code', monospace;
  --radius: 0.5rem;

  /* Kanban-specific variables */
  --glass-bg: hsla(0, 0%, 100%, 0.8);
  --glass-border: hsla(220, 13%, 91%, 0.5);
  --gradient-bg-light: linear-gradient(135deg, hsl(215, 100%, 96%) 0%, hsl(0, 0%, 100%) 50%, hsl(270, 100%, 97%) 100%);
  --card-shadow: 0 4px 6px -1px hsla(221, 83%, 53%, 0.1), 0 2px 4px -1px hsla(221, 83%, 53%, 0.06);
  --card-shadow-hover: 0 20px 40px hsla(0, 0%, 0%, 0.1), 0 0 20px hsla(221, 83%, 53%, 0.2);
}

.dark {
  --background: hsl(224, 71%, 4%);
  --foreground: hsl(213, 31%, 91%);
  --card: hsl(224, 71%, 10%);
  --card-foreground: hsl(213, 31%, 85%);
  --popover: hsl(224, 71%, 4%);
  --popover-foreground: hsl(213, 31%, 91%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(215, 25%, 27%);
  --secondary-foreground: hsl(213, 31%, 91%);
  --muted: hsl(223, 47%, 11%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(216, 100%, 9%);
  --accent-foreground: hsl(221, 83%, 53%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(216, 34%, 17%);
  --input: hsl(216, 34%, 17%);
  --ring: hsl(221, 83%, 53%);
  --chart-1: hsl(221, 83%, 53%);
  --chart-2: hsl(160, 100%, 36%);
  --chart-3: hsl(43, 96%, 56%);
  --chart-4: hsl(147, 78%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --sidebar: hsl(224, 71%, 10%);
  --sidebar-foreground: hsl(213, 31%, 85%);
  --sidebar-primary: hsl(221, 83%, 53%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(216, 100%, 9%);
  --sidebar-accent-foreground: hsl(221, 83%, 53%);
  --sidebar-border: hsl(216, 34%, 17%);
  --sidebar-ring: hsl(221, 83%, 53%);
  --font-sans: 'Inter', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Fira Code', monospace;
  --radius: 0.5rem;

  /* Kanban-specific dark variables */
  --glass-bg: hsla(224, 71%, 10%, 0.8);
  --glass-border: hsla(216, 34%, 17%, 0.5);
  --gradient-bg-dark: linear-gradient(135deg, hsl(224, 71%, 4%) 0%, hsl(224, 71%, 10%) 50%, hsl(271, 100%, 8%) 100%);
  --card-shadow: 0 4px 6px -1px hsla(0, 0%, 0%, 0.3), 0 2px 4px -1px hsla(0, 0%, 0%, 0.2);
  --card-shadow-hover: 0 20px 40px hsla(0, 0%, 0%, 0.3), 0 0 20px hsla(271, 91%, 65%, 0.3);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    height: 100%;
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
    min-height: 100vh;
    min-height: 100dvh; /* Dynamic viewport height for mobile */
    height: 100%;
    position: relative;
  }

  #root {
    min-height: 100vh;
    min-height: 100dvh; /* Dynamic viewport height for mobile */
    height: 100%;
    width: 100%;
    position: relative;
  }
}

@layer components {
  /* Glass morphism effect */
  .glass-effect {
    background: var(--glass-bg);
    backdrop-filter: blur(10px);
    border: 1px solid var(--glass-border);
  }

  /* Smooth drag and drop */
  [data-rbd-draggable-id] {
    transition: none !important;
    position: static !important;
  }

  /* Mobile viewport fixes */
  .mobile-scroll {
    -webkit-overflow-scrolling: touch;
    overflow-y: auto;
  }

  /* Fix for mobile viewport height issues */
  @supports (-webkit-touch-callout: none) {
    .min-h-screen {
      min-height: -webkit-fill-available;
    }
  }

  [data-rbd-draggable-id][data-rbd-drag-handle-draggable-id] {
    cursor: grab;
  }

  [data-rbd-draggable-id][data-rbd-drag-handle-draggable-id]:active {
    cursor: grabbing;
  }

  /* Fix positioning for scrollable containers */
  [data-rbd-droppable-id] {
    position: static !important;
  }

  /* Ensure proper positioning calculation */
  [data-rbd-drag-handle-context-id] {
    position: relative;
  }

  /* Card hover glow effect */
  .card-hover-glow {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .card-hover-glow:hover {
    box-shadow: var(--card-shadow-hover);
  }

  /* Scrollbar styling */
  .overflow-y-auto::-webkit-scrollbar {
    width: 6px;
  }

  .overflow-y-auto::-webkit-scrollbar-track {
    @apply bg-transparent;
  }

  .overflow-y-auto::-webkit-scrollbar-thumb {
    @apply bg-gray-300 dark:bg-gray-600 rounded-full;
  }

  .overflow-y-auto::-webkit-scrollbar-thumb:hover {
    @apply bg-gray-400 dark:bg-gray-500;
  }

  /* Line clamp utilities */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
}

@layer utilities {
  /* Custom animations */
  @keyframes fade-in {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slide-up {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes glow-pulse {
    0%, 100% {
      box-shadow: 0 0 20px hsla(221, 83%, 53%, 0.3);
    }
    50% {
      box-shadow: 0 0 30px hsla(221, 83%, 53%, 0.6);
    }
  }

  @keyframes shake {
    0%, 100% { transform: translateX(0); }
    25% { transform: translateX(-2px); }
    75% { transform: translateX(2px); }
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.3s ease-out;
  }

  .animate-glow-pulse {
    animation: glow-pulse 2s ease-in-out infinite;
  }

  .animate-shake {
    animation: shake 0.5s ease-in-out;
  }

  /* Backdrop effects */
  .backdrop-blur-sm {
    backdrop-filter: blur(4px);
  }

  .backdrop-blur-md {
    backdrop-filter: blur(12px);
  }

  /* Gradient text */
  .gradient-text {
    background: linear-gradient(135deg, hsl(221, 83%, 53%), hsl(271, 91%, 65%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

/* Focus visible improvements */
.focus-visible\:ring-2:focus-visible {
  outline: 2px solid transparent;
  outline-offset: 2px;
  box-shadow: 0 0 0 2px hsl(var(--ring));
}

/* Improved transitions - exclude drag elements */
*:not([data-rbd-draggable-id]):not([data-rbd-drag-handle-draggable-id]):not([data-rbd-droppable-id]) {
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
}

/* Disable all transforms and transitions during drag */
[data-rbd-drag-handle-context-id] * {
  transition: none !important;
}

/* Simple drag cursor styling */
[data-rbd-drag-handle-draggable-id] {
  cursor: grab !important;
}

[data-rbd-drag-handle-draggable-id]:active {
  cursor: grabbing !important;
}

/* Smooth dragging - reduce jankiness */
[data-rbd-draggable-id] {
  transition: none !important;
}

/* Ensure smooth drag animations */
[data-rbd-draggable-id] * {
  transition: none !important;
}

/* Mobile-first responsive design */
@media (max-width: 640px) {
  /* Mobile optimizations */
  body {
    -webkit-text-size-adjust: 100%;
    -webkit-tap-highlight-color: transparent;
    touch-action: manipulation;
  }

  /* Improve touch targets */
  button, [role="button"] {
    min-height: 44px;
    min-width: 44px;
    touch-action: manipulation;
  }

  /* Better scrolling on mobile */
  .mobile-scroll {
    -webkit-overflow-scrolling: touch;
    scroll-behavior: smooth;
  }

  /* Prevent zoom on input focus */
  input, select, textarea {
    font-size: 16px;
  }

  /* Mobile-specific card spacing */
  .task-card-mobile {
    margin-bottom: 12px;
    padding: 16px;
    border-radius: 12px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }

  /* Sticky header on mobile */
  .mobile-header {
    position: sticky;
    top: 0;
    z-index: 50;
    backdrop-filter: blur(12px);
  }
}

/* Tablet optimizations */
@media (min-width: 641px) and (max-width: 1024px) {
  /* Tablet-specific layout */
  .tablet-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1.5rem;
  }

  /* Touch-friendly buttons on tablet */
  button {
    min-height: 40px;
    touch-action: manipulation;
  }
}

/* Desktop optimizations */
@media (min-width: 1025px) {
  /* Hover effects only on desktop */
  .desktop-hover:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  }

  /* Better scrollbars on desktop */
  .custom-scrollbar::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.05);
    border-radius: 4px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, 0.3);
  }
}

/* Enhanced animations for better performance */
@media (prefers-reduced-motion: no-preference) {
  .smooth-animation {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .bounce-animation {
    animation: bounce 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }

  @keyframes bounce {
    0% { transform: scale(0.95) translateY(10px); opacity: 0; }
    50% { transform: scale(1.02) translateY(-2px); opacity: 0.8; }
    100% { transform: scale(1) translateY(0); opacity: 1; }
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .card-border {
    border-width: 2px;
    border-color: currentColor;
  }

  .text-muted {
    opacity: 0.8;
  }
}

/* Dark mode improvements */
.dark {
  --shadow-color: rgba(0, 0, 0, 0.5);
  --glow-color: rgba(255, 255, 255, 0.1);
}

.dark .dark-glow {
  box-shadow: 0 0 20px var(--glow-color);
}

/* Better dark mode gradients on mobile */
@media (max-width: 640px) {
  .dark {
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%) !important;
  }

  .dark .mobile-header {
    background: rgba(15, 23, 42, 0.9) !important;
    backdrop-filter: blur(16px);
  }

  .dark .mobile-scroll {
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
  }
}

/* Focus improvements for accessibility */
*:focus-visible {
  outline: 2px solid rgb(59 130 246);
  outline-offset: 2px;
  border-radius: 4px;
}

/* Better mobile menu */
@media (max-width: 640px) {
  .mobile-menu {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(8px);
    z-index: 9999;
  }

  .mobile-menu-content {
    position: absolute;
    bottom: 0;
