    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/boards", boardId, "columns"] });
      toast({ title: "Column created successfully" });
    },
    onError: () => {
      toast({ title: "Failed to create column", variant: "destructive" });
    },
