import bcrypt from 'bcryptjs';
import { eq } from 'drizzle-orm';
import { db } from './db';
import { users, sessions, type AuthUser, type AuthSession } from '../shared/auth-schema';
import config from './config';
import { nanoid } from 'nanoid';

export class AuthService {
  // Ð ÐµÐ³Ð¸ÑÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  static async register(email: string, password: string, firstName: string, lastName: string): Promise<AuthUser> {
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÐµÑ‚ Ð»Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ
    const existingUser = await db.select().from(users).where(eq(users.email, email)).limit(1);
    if (existingUser.length > 0) {
      throw new Error('User already exists');
    }

    // Ð¥ÐµÑˆÐ¸Ñ€ÑƒÐµÐ¼ Ð¿Ð°Ñ€Ð¾Ð»ÑŒ
    const passwordHash = await bcrypt.hash(password, config.auth.bcryptRounds);

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    const [user] = await db.insert(users).values({
      id: nanoid(),
      email,
      passwordHash,
      firstName,
      lastName,
    }).returning();

    return user;
  }

  // ÐÐ²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  static async login(email: string, password: string): Promise<{ user: AuthUser; sessionId: string }> {
    // ÐÐ°Ñ…Ð¾Ð´Ð¸Ð¼ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    const [user] = await db.select().from(users).where(eq(users.email, email)).limit(1);
    if (!user) {
      throw new Error('Invalid credentials');
    }

    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð¿Ð°Ñ€Ð¾Ð»ÑŒ
    const isValidPassword = await bcrypt.compare(password, user.passwordHash);
    if (!isValidPassword) {
      throw new Error('Invalid credentials');
    }

    // Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ ÑÐµÑÑÐ¸ÑŽ
    const sessionId = nanoid();
    const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 Ñ‡Ð°ÑÐ°

    await db.insert(sessions).values({
      id: sessionId,
      userId: user.id,
      expiresAt,
    });

    return { user, sessionId };
  }

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð¿Ð¾ ÑÐµÑÑÐ¸Ð¸
  static async getUserBySession(sessionId: string): Promise<AuthUser | null> {
    if (!sessionId) return null;

    const [session] = await db.select().from(sessions).where(eq(sessions.id, sessionId)).limit(1);
    if (!session || session.expiresAt < new Date()) {
      // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð¸ÑÑ‚ÐµÐºÑˆÑƒÑŽ ÑÐµÑÑÐ¸ÑŽ
      if (session) {
        await db.delete(sessions).where(eq(sessions.id, sessionId));
      }
      return null;
    }

    const [user] = await db.select().from(users).where(eq(users.id, session.userId)).limit(1);
    return user || null;
  }

  // Ð’Ñ‹Ñ…Ð¾Ð´ Ð¸Ð· ÑÐ¸ÑÑ‚ÐµÐ¼Ñ‹
  static async logout(sessionId: string): Promise<void> {
    if (sessionId) {
      await db.delete(sessions).where(eq(sessions.id, sessionId));
    }
  }

  // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  static async updateProfile(userId: string, updates: Partial<Pick<AuthUser, 'firstName' | 'lastName' | 'profileImageUrl'>>): Promise<AuthUser> {
    const [user] = await db.update(users)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();

    if (!user) {
      throw new Error('User not found');
    }

    return user;
  }

  // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° Ð¸ÑÑ‚ÐµÐºÑˆÐ¸Ñ… ÑÐµÑÑÐ¸Ð¹
  static async cleanupExpiredSessions(): Promise<void> {
    await db.delete(sessions).where(eq(sessions.expiresAt, new Date()));
  }
}

