import config from './config';

// Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¿Ð¾Ð´ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ Ðº PostgreSQL
const connectionString = process.env.DATABASE_URL || 'postgresql://localhost:5432/taskflow';

